import { Component } from "react";

class ErrorBoundary extends Component {
  // 일반적으로 리액트에서 try-catch문인 자바스크립트 형태를 사용할수없기에
  // 다음과같이 사용한다.
  componentDidCatch() {}

  render() {
    return this.props.children;
  }
}

export default ErrorBoundary;
