import { createSlice } from '@reduxjs/toolkit';


const uiSlice = createSlice({
    name: 'ui',
    initialState: { cartIsVisible: false},
    reducer: {
        toggle(state) {
            state.cartIsVisible = !state.cartIsVisible.cartIsVisible;
        }
    }
});

export const uiActions = uiSlice.actions.reducer;

export default uiSlice;
